def get_features():
    return features
    

def make_data(path,split,feature_length,feature_map,lengthfrac):
    
    files = os.listdir(path)
    random.shuffle(files)
    files = files[:int(len(files) * lengthfrac) - 1]
    
    n = len(files)
    split_index = int(split * n) - 1
    
    Train_X = np.zeros(shape=(split_index + 1,feature_length))
    Test_X = np.zeros(shape=(n - split_index - 1,feature_length))
    
    for i in range(0,split_index):
        msg = ex.get_mail(path + '/' + files[i])
        words = ex.tokenize(msg)
        for word in words:
            if word in feature_map:
                Train_X[i][feature_map[word]] = 1
                
    for i in range(split_index + 1,n-1):
        msg = ex.get_mail(path + '/' + files[i])
        words = ex.tokenize(msg)
        for word in words:
            if word in feature_map:
                Test_X[i-split_index-1][feature_map[word]] = 1
             
    return Train_X, Test_X


def get_data(basepath,split,lengthfrac):
    
    feature_map = {}
    for i in range(len(features)):
        feature_map[features[i]] = i
      
    feature_length = len(features)
    
    path = basepath + '/ham'
    Train_X_ham, Test_X_ham = make_data(path, split, feature_length,feature_map,lengthfrac)
    Train_Y_ham = np.zeros(shape=(Train_X_ham.shape[0],1))
    Test_Y_ham = np.zeros(shape=(Test_X_ham.shape[0],1))
    
    path = basepath + '/spam'
    Train_X_spam, Test_X_spam = make_data(path, split, feature_length,feature_map,lengthfrac)
    Train_Y_spam = np.ones(shape=(Train_X_spam.shape[0],1))
    Test_Y_spam = np.ones(shape=(Test_X_spam.shape[0],1))
          
    Train_X = np.concatenate([Train_X_ham,Train_X_spam],axis=0)   
    Train_Y = np.concatenate([Train_Y_ham,Train_Y_spam],axis=0)   
    
    Test_X = np.concatenate([Test_X_ham,Test_X_spam],axis=0)   
    Test_Y = np.concatenate([Test_Y_ham,Test_Y_spam],axis=0)   
    
    return Train_X, Train_Y, Test_X, Test_Y

